import React, { useEffect, useState } from "react";
import '../assets/App.css';
import Header from "./Header"
import Home from "./Home"
import Profile from "./Profile"
import SignIn from "./SignIn"
import SignUp from "./SignUp"
import NasaDetails from "./NasaDetails"
import NewApodForm from "./NewApodForm"
import ProfileDetails from "./ProfileDetails"

/* Import Route and Switch properties from react-router dom.
 The former allows assigned routing to varying components 
model parent/child branch behavior as a sitemap, while
the former allow us the assignment itself. */
// Tip: don't forget to run npm install react-router dom !
import { Route, Switch, useHistory } from 'react-router-dom'

const apiKey = "quqeczazJ4D6huLcVhNJljA58RhApMCyv3KleLZk"

function App() {
  const [apodArray, setApodArray] = useState([])
  const [apodArrayParse, setApodArrayParse ] = useState(0)
  const [apodSelectionArray, setApodSelectionArray] = useState([])
  const [isLoggedIn, setLogIn] = useState(false)
  const [currentProfile, setCurrentProfile] = useState(null)
  const [isLoadedImages, setIsLoadedImages] = useState(false)
  const [nasaImage, setNasaImage] = useState(null)
  // const [teamArray, setTeamArray] = useState([])
  // const [displayArray, setDisplayArray] = useState([])

  // const [teamMemberArrayfromDB, setTeamMemberArrayfromDB] = ([])

  const history = useHistory();

  //callback function pass down to RecruitDetail page for the Enlist Btn
  let flag = true;
  const onApodSelection = (selectedApod) => {
      setApodSelectionArray([...apodSelectionArray, selectedApod])

      if (apodSelectionArray.length === 0 ) {
        setApodSelectionArray([...apodSelectionArray, selectedApod])
        console.log(apodSelectionArray)
      } else { apodSelectionArray.map(apod => {
        // console.log(hero.id, selectedHero.id)
                if (apod.id === selectedApod.id) {
                    flag = false; 
          }
    })
    if (flag){
      setApodSelectionArray([...apodSelectionArray, selectedApod])

    }
  }
    //add hero to the heroSelectionArray 
    // if (selectedHero.id)

    //make this herocard disappear from RecruitList
    const apodArrayAfterSelect = apodArray.filter(apod=>apod.id !== selectedApod.id)
    setApodArray(apodArrayAfterSelect)
  }

  //callback fn pass down to HeroSelection.js for AddToTeamBtn
  const onAddToProfileBtnClick = (apodSelectionArray) => {
    // setTeamArray([...teamArray, heroSelectionArray])
    console.log(apodSelectionArray)

    apodSelectionArray.map(apod => {
      fetch("http://localhost:3000/teamMember", {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          userId: currentProfile,
          date: apod.date,
          title: apod.title,
          url: apod.url,
          explanation: apod.explanation
          })
      })
      // .then(history.push("/team"))
      .catch(error => console.error('Error:', error))
    })    

    //clear out heroSelectionArray 
    setApodSelectionArray([]);
    history.push("/team")
  }

  //handle disselectBtn click on HeroSelection
  const onDisselectBtnClickInSelection = (disselectedApod) => {
    //change the heroSelectionArray (disappear on the selection section)
    setApodSelectionArray(apodSelectionArray.filter(selectedApod => selectedApod.name !== disselectedApod.name))

    //change the heroArray (display on the recruit list section)
    setApodArray([...apodArray, disselectedApod])
  }




  const handleLogIn = (signedInProfile) => {
    setLogIn(true)
    setCurrentProfile(signedInProfile)
  }

  useEffect(() => {
    // fetch('http://localhost:3000/heroes')
    fetch(`https://api.nasa.gov/planetary/apod?api_key=${apiKey}&start_date=2021-01-01&end_date=2021-12-31`)
    // fetch('https://gateway.marvel.com:443/v1/public/characters?apikey=c8d257c5c8de3331d6de741ea71c6a3a')
    .then(response => response.json())
    .then(apodData => {
      console.log(apodData)
      setNasaImage(apodData)
      setIsLoadedImages(true)
      // let heroes = heroData.data.results.map(hero => hero)
      // setHeroArray(heroes)
      // setIsLoadedHeroes(true)
    })
    // .then(heroData => setHeroArray(heroData.data.map(results => results.hero)))
    // .then(heroData => heroData.data.results.map(hero => console.log(hero)))
    // .then(heroData => setHeroArray(heroData))
  }, [apodArrayParse])

  if (!isLoadedImages) return <h2>Loading...</h2>

  // useEffect(() => {
  //   fetch('http://localhost:3000/heroes')
  //   // fetch('https://gateway.marvel.com:443/v1/public/characters?name=Deadpool&apikey=c8d257c5c8de3331d6de741ea71c6a3a')
  //   .then(response => response.json())
  //   // .then(heroData => console.log(heroData))
  //   .then(heroData => setHeroArray(heroData))
  // }, [])


  // // Might need help from Angelo for this one
  // teamArray.map(hero => console.log(hero))
  // console.log(heroArray[1])
  // // console.log(heroArray)
  // // setHeroArray(heroArray.filter(individual => individual.id !== hero.id))
  // const displayArray = heroArray.filter((hero) => {
  //   let teamArrayHeroes = teamArray.map(hero => hero)
  //   return hero.id !== teamArrayHeroes.heroId
  // })

  return (
    <div >
      <Header isLoggedIn={isLoggedIn} setLogIn={setLogIn} currentProfile={currentProfile} />
      <Switch>

        <Route exact path="/" component={() => <Home apodArray={apodArray} 
                                                    //  displayArray={displayArray}
                                                     nasaImage={nasaImage}
                                                     apodSelectionArray={apodSelectionArray} 
                                                     onAddToProfileBtnClick={onAddToProfileBtnClick}
                                                     onDisselectBtnClickInSelection={onDisselectBtnClickInSelection}
                                                     apodArrayParse={apodArrayParse}
                                                     setApodArrayParse={setApodArrayParse}
                                                     isLoadedImages={isLoadedImages}
                                                     currentProfile={currentProfile}
                                                     /> }  />
        <Route path="/recruit/:id" component={() => <NasaDetails apiKey={apiKey} isLoggedIn={isLoggedIn} onApodSelection={onApodSelection} setApodArray={setApodArray} apodArray={apodArray}/> }  />
        <Route path="/team/:id" component={() => <ProfileDetails onApodSelection={onApodSelection} /> }  />
        <Route exact path="/team" component={() => <Profile currentProfile={currentProfile} isLoggedIn={isLoggedIn} /*teamArray={teamArray} setTeamArray={setTeamArray}*/
                                                      /> }  />
        <Route exact path="/addhero" component={() => <NewApodForm currentProfile={currentProfile} isLoggedIn={isLoggedIn}/> }  />
        {/* <Route path="/team/:id" component={() => <RecruitDetails /> }  /> */}
        <Route exact path="/signin" component={() => <SignIn onExistingProfileLogIn={handleLogIn} /> }  />
        <Route exact path="/signup" component={() => <SignUp onNewProfileSubmit={handleLogIn} /> }  />
      </Switch>


      {/* <Home heroArray={heroArray} />

      <Team />

      <SignIn />

      <SignUp /> */}

    </div>
  );
}

export default App;
