import React from "react";
import ApodCard from "./ApodCard"
import NasaDetails from "./NasaDetails"
//import things for css
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  }
}));

function NasaList({ isLoadedImages, apodArray, onApodSelection, apodArrayParse, setApodArrayParse, nasaImage}) {
  const classes = useStyles();

  //props to pass down for css purpose
  const xsNum = 6;
  const smNum = 2;

  //props that pass that to hide the button
  const disselectBtnId = "disselectBtnId"

  const handleBack = () => {
    setApodArrayParse(apodArrayParse - 96)
  }

  const handleNext = () => {
    // setApodArrayParse(apodArrayParse + 96)
    setApodArrayParse(apodArrayParse + 1)
  }

  if (!isLoadedImages) return <h2>Loading...</h2>

  return (
    <div className="flex-container">

        <div className={classes.root}>

          {/* {apodArrayParse > 0 ? <button onClick={handleBack} className="back-btn-in-list">Back</button> : null} */}
          {/* <button onClick={handleBack}>Back</button> */}
          {apodArrayParse < 1400 ? <button onClick={handleNext} className="next-btn-in-list">Randomize Selection</button> : null}
          {/* <button onClick={handleNext}>Next</button> */}

          <Grid container spacing={3}>
            {nasaImage.map(apod => <ApodCard 
                                              key={apod.date} 
                                              apod={apod} 
                                              apodTitle = {apod.title}
                                              apodId={apod.date}
                                              onApodSelection={onApodSelection}
                                              xsNum={xsNum}
                                              smNum={smNum}
                                              disselectBtnId={disselectBtnId}
                                              apodImage={apod.url}
                                              link={`/recruit/${apod.date}`}
                                              />)}
          </Grid>
          {/* <Grid container spacing={3}>
            {heroArray.map(hero => <HeroCard 
                                              key={hero.id} 
                                              hero={hero} 
                                              heroId={hero.id}
                                              onHeroSelection={onHeroSelection}
                                              xsNum={xsNum}
                                              smNum={smNum}
                                              disselectBtnId={disselectBtnId}
                                              heroImage={hero.thumbnail.path + "." + hero.thumbnail.extension}
                                              link={`/recruit/${hero.id}`}
                                              />)}
          </Grid> */}

          {/* {apodArrayParse > 0 ? <button onClick={handleBack} className="back-btn-in-list">Back</button> : null} */}
          {/* <button onClick={handleBack}>Back</button> */}
          {apodArrayParse < 1400 ? <button onClick={handleNext} className="next-btn-in-list">Generate New Selection</button> : null}
          {/* <button onClick={handleNext}>Next</button> */}

        </div>

    </div>
  );
}

export default NasaList;
